// jQuery-cookie is deprecated
// we'll now use js-cookie
// this script intends to add backward compatibility
// for apps who still use jQuery-cookie

(function ( $ ) {
    $.cookie = function( param1,param2,param3 ) {
      if(!param1){
        return Cookies.get();
      }
      else{
        if(!param2){
          if(param2==''){
            return Cookies.remove(param1);
          }
          else{
            return Cookies.get(param1);
          }
        }
        else {
          return Cookies.set(param1,param2,param3)
        }
      }
    };
    $.removeCookie = function( param1 ) {
      if(param1){
        return Cookies.remove(param1);
      }
      else {
        return false;
      }
    };
}( jQuery ));
