<template>
    <div :id="`js-___componentname____--${id}`" class="___componentname____"></div>
</template>

<script>
    import * as COMMON from 'common';
    import JSComponent from './index.js';

    export default {
        name: "cotui-___componentname____",
        inheritAttrs: false,
        components: {},
        props: {},
        data(){
            return {
                id: this.$attrs.id||COMMON.utils.generateID(),
                component(){
                    return new JSComponent({target: this.$el, attr: this.$attrs})
                } 
            }
        },
        created(){
            this.component().init()
        },
        mounted(){
            this.component().render()
        }
    }
</script>

<style lang="scss" scoped>
    @import './resources/style.scss';
</style>