import Vue from 'vue';

/**
 * Initialize COTUI.___COMPONENTNAME____
 * @property {string} options                
 * @property {string} options.target        - initialize components contained in the target.
 * @property {string} options.type          - '', addressLookup'
 * @property {object} callback               
 * 
 * @return {object} _view
 *
 * 
*/

const ___COMPONENTNAME____ = function (options={}, callback={}){
    const el = document.getElementById(options.target) || this.target;
    const element = this._initCustomElements(el,'cotui-___componentname____')

    const container = document.createElement('div');
    const $el = el.appendChild(container);

    const component = new Vue({
        render(createElement){
       
            return createElement(element,{
                props:{},
                attrs:{},
                data:{},
            })
       
        }
    }).$mount($el)


    return{
        _view: component,
    }
}

export default ___COMPONENTNAME____