const req = require.context('../src/stories', true, /\.js$/)

function loadStories() {
  req.keys().forEach((filename) => req(filename))
}

addDecorator(story=> {
  return {
    template:`
    <div class="container" style="padding: 1em">
      <story />
    </div>
    `
  }
});


configure(loadStories, module);
